import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
//import logo from './logo.svg';
import axios from 'axios';

//Pages
import Navbar from 'components/UI/navbar.jsx';
import Content from 'components\UI/content.jsx';
import Header from 'components\UI\header.jsx';

const API_URL = 'http://127.0.0.1:5000/expenses';

function App() {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({ description: '', amount: '', category: '', date: '' });
  const [editingId, setEditingId] = useState(null);

  const fetchExpenses = async () => {
    setLoading(true);
    try {
      const response = await axios.get(API_URL);
      setExpenses(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching data: ', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const addExpense = async () => {
    if (!editingId) {
      try {
        const response = await axios.post(API_URL, form);
        setExpenses([...expenses, response.data]);
        setForm({ description: '', amount: '', category: '', date: '' });
      } catch (error) {
        console.error('Error adding expense: ', error);
      }
    } else {
      try {
        const response = await axios.put(`${API_URL}/${editingId}`, form);
        const updatedExpenses = expenses.map(exp => exp.id === editingId ? response.data : exp);
        setExpenses(updatedExpenses);
        setEditingId(null);
        setForm({ description: '', amount: '', category: '', date: '' });
      } catch (error) {
        console.error('Error updating expense: ', error);
      }
    }
  };

  const editExpense = (expense) => {
    setForm(expense);
    setEditingId(expense.id);
  };

  const deleteExpense = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      setExpenses(expenses.filter(exp => exp.id !== id));
    } catch (error) {
      console.error('Error deleting expense: ', error);
    }
  };

  return (
    <Router>
      <div className="App">
        <Header />
        <Navbar />
        <Content>
          <div>
            <h2>Expense Tracker</h2>
          </div>
        </Content>
        </div>
        </Router>
  );
}

export default App;
