import React from 'react';
import { Link } from 'react-router-dom';

// Импортировать иконку компонента, если вы используете компоненты иконок,
// например из react-icons
import { IconContext } from 'react-icons';

// Отдельный компонент для пункта меню с иконкой
const NavItem = ({ to, icon: Icon, children }) => (
  <li className="nav-item">
    <Link className="nav-link" to={to}>
      {/* Обернуть иконку и текст в контекст для установки стиля */}
      <IconContext.Provider value={{ className: "nav-icon" }}>
        {/* Рендер иконки, если она предоставлена */}
        {Icon && <Icon />}
        {children}
      </IconContext.Provider>
    </Link>
  </li>
);

export default NavItem;
