import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Layout from './components/';
import WelcomePage from './pages/Welcome.jsx';
import SignupPage from './pages/signup.jsx';
import LoginPage from './pages/login.jsx';
import Forgot_passwordPage from './pages/Forgot_password.jsx';
import OverviewPage from './pages/Overview.jsx';
import BalancesPage from './pages/Balances.jsx';
import Balance_detailsPage from './pages/Balance_details.jsx';
import BillsPage from './pages/Bills.jsx';
import ExpencesPage from './pages/Expences.jsx';
import GoalsPage from './pages/Goals.jsx';
import SettingsPage from './pages/Setting.jsx';
import './styles/app.css';
import { fetchExpenses, addExpense, updateExpense, deleteExpense } from './api';

function App() {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({ description: '', amount: '', category: '', date: '' });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const data = await fetchExpenses();
        setExpenses(data);
      } catch (error) {
        console.error('Error fetching data: ', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Остальной код остается без изменений

  return (
    <Router>
    <div className="App">
      <Header />
      <Navbar />
      <Routes>
        <Route path="/" element={<WelcomePage />} />
        <Route path="/signup" element={<SignupPage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/forgot-password " element={<Forgot_passwordPage />} />
        <Layout>
        <Route path="/overview" element={<OverviewPage />} />
        <Route path="/balances" element={<BalancesPage />} />
        <Route path="/balance-details" element={<Balance_detailsPage />} />
        <Route path="/bills" element={<BillsPage />} />
        <Route path="/expences" element={<ExpencesPage />} />
        <Route path="/goals" element={<GoalsPage />} />
        <Route path="/settings" element={<SettingsPage />} />
        </Layout>
      </Routes>
    </div>
  </Router>
  );
}

export default App;
