import React from 'react';
import '../../styles/UserProfileButton.sass';
import { Link } from 'react-router-dom';


const UserProfileButton = ({ username, profilePhoto }) => {
  // Дефолтное изображение, если фото профиля не предоставлено
  const defaultPhoto = profilePhoto || require('../../assets/images/User.png');

  return (
    <div className="user-profile">
      <img src={requiredefaultPhoto} alt="Profile" className="profile-photo"/>
      <div>
        <Link to="/settings">{username}</Link>
        <small>View profile</small>
      </div>
    </div>
  );
};

export default UserProfileButton;
