import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../../styles/header.sass';

// Компонент иконки календаря
const CalendarIcon = () => (
  <svg className="calendar-icon" width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M13 17L18 12L13 7" stroke="#9F9F9F" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
    <path d="M6 17L11 12L6 7" stroke="#9F9F9F" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
  </svg>
);

const Header = ({ showGreeting }) => {
    const location = useLocation();
    const currentDate = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
    const [searchTerm, setSearchTerm] = useState('');
    const [scrolled, setScrolled] = useState(false);

    // Функция обработки прокрутки
    useEffect(() => {
        const handleScroll = () => {
            const isScrolled = window.scrollY > 10;
            setScrolled(isScrolled);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    // Функция обработки поиска
    const handleSearch = (event) => {
        event.preventDefault();
        console.log('Search Term:', searchTerm);
        // Добавьте логику перехода на страницу поиска или фильтрации данных
    };

    return (
        <div className={`header ${scrolled ? 'scrolled' : ''}`}>
            {showGreeting && location.pathname === '/overview' ? (
                <div className="header-greeting">
                    <h1>Hello User</h1>
                    <span><CalendarIcon /> {currentDate}</span>
                </div>
            ) : (
                <span><CalendarIcon /> {currentDate}</span>
            )}
            <div className="header-search">
                <form onSubmit={handleSearch}>
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search here"
                        value={searchTerm}
                        onChange={(event) => setSearchTerm(event.target.value)}
                    />
                    <button type="submit" className="search-button" aria-label="Search">
                        Search
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Header;
