import React, { useState } from 'react';
import { useLocation } from 'react-router-dom'; // –î–ª—è –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è —Ç–µ–∫—É—â–µ–≥–æ –º–∞—Ä—à—Ä—É—Ç–∞
import '../../styles/header.sass';

const Header = ({ showGreeting }) => {
    const location = useLocation();
    const currentDate = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
    const [searchTerm, setSearchTerm] = useState('');

    const CalendarIcon = () => (
        <svg className="calendar-icon" width="24" height="24" viewBox="0 0 24 24">
            {<svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M13 17L18 12L13 7" stroke="#9F9F9F" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" />
                <path d="M6 17L11 12L6 7" stroke="#9F9F9F" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" />
            </svg>
            }
        </svg>
    );

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∏–∑–º–µ–Ω–µ–Ω–∏—è –≤ –ø–æ–ª–µ –ø–æ–∏—Å–∫–∞
    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∑–∞–ø—Ä–æ—Å–∞ –ø–æ–∏—Å–∫–∞
    const handleSearch = (event) => {
        event.preventDefault();
        // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –ª–æ–≥–∏–∫—É –ø–æ–∏—Å–∫–∞ –∏–ª–∏ —Ä–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É –ø–æ–∏—Å–∫–∞ —Å –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã–º –ø–æ–∏—Å–∫–æ–≤—ã–º –∑–∞–ø—Ä–æ—Å–æ–º
        console.log('Search Term:', searchTerm);
    };

    return (
        <div className="header">
            {showGreeting && location.pathname === '/overview' ? (
                <div className="header-greeting">
                    <h1>Hello Tanzir</h1>
                    <span><CalendarIcon /> {currentDate}</span>
                </div>
            ) : (
                <span><CalendarIcon /> {currentDate}</span>
            )}
            <div className="header-search">
                {/* –î–æ–±–∞–≤–ª—è–µ–º —Ñ–æ—Ä–º—É –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –ø–æ–∏—Å–∫–∞ */}
                <form onSubmit={handleSearch}>
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search here"
                        value={searchTerm}
                        onChange={handleSearchChange}
                    />
                    {/* –ö–Ω–æ–ø–∫–∞ –ø–æ–∏—Å–∫–∞ */}
                    <button type="submit" className="search-button">
                        üîç
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Header;
